CREATE PROCEDURE SpInsertaaGrupoLabor @empresa int,@activo bit,@codigo varchar(50),@descripcion varchar(50),@Retorno int output  AS begin tran aGrupoLabor insert aGrupoLabor( empresa,activo,codigo,descripcion ) select @empresa,@activo,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran aGrupoLabor end else begin set @Retorno = 1 rollback tran aGrupoLabor end