CREATE PROCEDURE SpInsertacEstructuraCCosto @empresa int,@nivel int,@inicio int,@tamaño int,@total int,@activo bit,@descripcion varchar(550),@Retorno int output  AS begin tran cEstructuraCCosto insert cEstructuraCCosto( empresa,nivel,inicio,tamaño,total,activo,descripcion ) select @empresa,@nivel,@inicio,@tamaño,@total,@activo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran cEstructuraCCosto end else begin set @Retorno = 1 rollback tran cEstructuraCCosto end