CREATE PROCEDURE SpInsertacxpClaseProveedor @empresa int,@descripcion varchar(950),@codigo char(4),@Retorno int output  AS begin tran cxpClaseProveedor insert cxpClaseProveedor( empresa,descripcion,codigo ) select @empresa,@descripcion,@codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran cxpClaseProveedor end else begin set @Retorno = 1 rollback tran cxpClaseProveedor end