CREATE PROCEDURE SpInsertagTipoTransaccionDias @empresa int,@lunes bit,@martes bit,@miercoles bit,@jueves bit,@viernes bit,@sabado bit,@domingo bit,@tipo varchar(50),@Retorno int output  AS begin tran gTipoTransaccionDias insert gTipoTransaccionDias( empresa,lunes,martes,miercoles,jueves,viernes,sabado,domingo,tipo ) select @empresa,@lunes,@martes,@miercoles,@jueves,@viernes,@sabado,@domingo,@tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoTransaccionDias end else begin set @Retorno = 1 rollback tran gTipoTransaccionDias end