CREATE PROCEDURE [dbo].[SpInsertanCuadrillaFuncionario] @empresa int,@funcionario int,@cuadrilla varchar(50),@Retorno int output  AS begin tran nCuadrillaFuncionario insert nCuadrillaFuncionario( empresa,funcionario,cuadrilla ) select @empresa,@funcionario,@cuadrilla if (@@error = 0 ) begin set @Retorno = 0 commit tran nCuadrillaFuncionario end else begin set @Retorno = 1 rollback tran nCuadrillaFuncionario end