CREATE PROCEDURE [dbo].[SpInsertanEmbargos] @empresa int,@añoInicial int,@empleado int,@codigo int,@año int,@mes int,@saldoCuotas int,@empresaEmbarga int,@embargante int,@cuotasCobroPosterior int,@cuotas int,@valorCuotas money,@porcentaje money,@valorFinal money,@valorCobroPosterior money,@valorBase money,@saldo money,@valorFinalPosterior money,@fecha datetime,@fechaRegistro datetime,@activo bit,@cobroPosterior bit,@manejaCuota bit,@manejaCuotaPosterior bit,@manejaSaldo bit,@pCobroPosterior decimal,@tipo varchar(50),@mandamiento varchar(50),@periodoInicial varchar(6),@peridoFinal varchar(6),@usuario varchar(50),@tipoCuenta varchar(50),@banco varchar(50),@cuentaBancaria varchar(50),@modoPago varchar(50),@fiscal varchar(200),@salarioMinimo bit,@Retorno int output  AS begin tran nEmbargos insert nEmbargos( empresa,empleado,codigo,año,mes,saldoCuotas,empresaEmbarga,embargante,cuotasCobroPosterior,cuotas,valorCuotas,porcentaje,valorFinal,valorCobroPosterior,valorBase,saldo,valorFinalPosterior,fecha,fechaRegistro,activo,cobroPosterior,manejaCuota,manejaCuotaPosterior,manejaSaldo,pCobroPosterior,tipo,mandamiento,periodoInicial,peridoFinal,usuario,tipoCuenta,banco,cuentaBancaria,modoPago,fiscal,añoInicial,salarioMinimo ) select @empresa,@empleado,@codigo,@año,@mes,@saldoCuotas,@empresaEmbarga,@embargante,@cuotasCobroPosterior,@cuotas,@valorCuotas,@porcentaje,@valorFinal,@valorCobroPosterior,@valorBase,@saldo,@valorFinalPosterior,@fecha,@fechaRegistro,@activo,@cobroPosterior,@manejaCuota,@manejaCuotaPosterior,@manejaSaldo,@pCobroPosterior,@tipo,@mandamiento,@periodoInicial,@peridoFinal,@usuario,@tipoCuenta,@banco,@cuentaBancaria,@modoPago,@fiscal,@añoInicial,@salarioMinimo if (@@error = 0 ) begin set @Retorno = 0 commit tran nEmbargos end else begin set @Retorno = 1 rollback tran nEmbargos end