CREATE PROCEDURE [dbo].[SpInsertanEntidadFondoPension] @empresa int,@tercero int,@fechaRegistro datetime,@activo bit,@integral bit,@pEmpleado decimal(18,2),@pEmpleador decimal(18,2),@pInactividad decimal(18,2),@pSolidaridad decimal(18,2),@codigo varchar(50),@descripcion varchar(550),@proveedor varchar(50),@codigoNacional varchar(50),@observacion varchar(5550),@usuario varchar(50),@cuenta varchar(50),@Retorno int output  AS begin tran nEntidadFondoPension insert nEntidadFondoPension( empresa,tercero,fechaRegistro,activo,integral,pEmpleado,pEmpleador,pInactividad,pSolidaridad,codigo,descripcion,proveedor,codigoNacional,observacion,usuario,cuenta ) select @empresa,@tercero,@fechaRegistro,@activo,@integral,@pEmpleado,@pEmpleador,@pInactividad,@pSolidaridad,@codigo,@descripcion,@proveedor,@codigoNacional,@observacion,@usuario,@cuenta if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadFondoPension end else begin set @Retorno = 1 rollback tran nEntidadFondoPension end