
CREATE PROCEDURE [dbo].[SpInsertapJerarquiaAnalisis] @jerarquia int,@prioridad int,@empresa bit,@resultado bit,@discreta bit,@campoResul bit,@resultadoParcial bit,@resultadoFinal bit,@analisis varchar(10),@formula varchar(8000),@expresion varchar(8000),@Retorno int output  AS begin tran pJerarquiaAnalisis insert pJerarquiaAnalisis( jerarquia,prioridad,empresa,resultado,discreta,campoResul,resultadoParcial,resultadoFinal,analisis,formula,expresion ) select @jerarquia,@prioridad,@empresa,@resultado,@discreta,@campoResul,@resultadoParcial,@resultadoFinal,@analisis,@formula,@expresion if (@@error = 0 ) begin set @Retorno = 0 commit tran pJerarquiaAnalisis end else begin set @Retorno = 1 rollback tran pJerarquiaAnalisis end