CREATE PROCEDURE SpInsertasUsuarios @empleado int,@fechaRegistro datetime,@activo bit,@clave varbinary,@usuario varchar(50),@descripcion varchar(250),@email varchar(250),@Retorno int output  AS begin tran sUsuarios insert sUsuarios( empleado,fechaRegistro,activo,clave,usuario,descripcion,email ) select @empleado,@fechaRegistro,@activo,@clave,@usuario,@descripcion,@email if (@@error = 0 ) begin set @Retorno = 0 commit tran sUsuarios end else begin set @Retorno = 1 rollback tran sUsuarios end